import React from 'react'
import "./CalendarGrid.css"
import { numOfDays, week } from '../../data/data';
import Header from '../Header/Header';
import isDayOff from 'isdayoff'


const CalendarGrid = () => {
    const days = [...Array(numOfDays)].map((el, ind) => {
        let day = new Date();
        day.setDate(ind);
        return day;
    });

    console.log(isDayOff);

    return (
        <>
            <Header/>
            <div className='weekdaysWrapper'>
                {week.map(element =>
                    <div className='weekday'>
                        {element}
                    </div>
                )}
            </div>

            <div className='gridWrapper'>
                {days.map((i) =>
                    <div key={i.toString()} className='cellWrapper'>
                        <div className='rowInCell'>
                            <div className='dayWrapper'>
                                {i.getDate()}
                            </div>

                        </div>
                    </div>
                )}
            </div>
        </>
    )
}

export default CalendarGrid
